package com.example.nagaa.dictionary;import android.speech.tts.TextToSpeech;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.ImageButton;import android.widget.ImageView;import android.widget.TextView;import android.widget.Toast;import java.util.ArrayList;import java.util.Locale;public class DefinitionActivity extends AppCompatActivity {    int position;    ImageView TTSimage;    ImageButton next,previous;    WordsList list;    ArrayList<WordsList> words;    TextView wordTV,defTV,exTv;    TextToSpeech textToSpeech;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_definition);        TTSimage=findViewById(R.id.tts);        previous=findViewById(R.id.prev);        next=findViewById(R.id.next);        TextToSpeech.OnInitListener listener=new TextToSpeech.OnInitListener() {            @Override            public void onInit(int i) {            }        };        Bundle bundle=getIntent().getExtras();        textToSpeech=new TextToSpeech(DefinitionActivity.this,listener);       words=bundle.getParcelableArrayList("ArrayList");       position=bundle.getInt("position");       activity(position);       previous.setOnClickListener(new View.OnClickListener() {           @Override           public void onClick(View view) {               if(position==0)               {                   Toast.makeText(DefinitionActivity.this,"The First word is here",Toast.LENGTH_LONG);                   activity(position);               }               else               {                   activity(position-1);                   --position;               }           }       });        next.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                if(position==words.size()-1)                {                    Toast.makeText(DefinitionActivity.this,"The Last word is here.More words soon",Toast.LENGTH_LONG);                    activity(position);                }                else                {                    activity(position+1);                    ++position;                }            }        });    }    private void activity(int position)    {        list =words.get(position);        wordTV=findViewById(R.id.word);        defTV=findViewById(R.id.def);        exTv=findViewById(R.id.ex);        wordTV.setText(list.getWord()+":");        exTv.setText("ex :"+list.getExample());        defTV.setText("def :"+list.getDefinition());        TTSimage.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                TTSimage.setBackgroundColor(66666);                textToSpeech.setLanguage(Locale.ENGLISH);                textToSpeech.setSpeechRate((float) 0.5);                textToSpeech.speak(list.getWord(),TextToSpeech.QUEUE_FLUSH,null);            }        });    }}